mapper.select:
SELECT a.prj_id 
     , a.version_number
     , a.step_number
     , a.file_path
FROM process_step_info_file a
WHERE a.prj_id = #{prj_id}
AND a.version_number = #{version_number}
AND a.step_number = #{step_number}

mapper.update:
UPDATE process_step_info
SET lstc_file_path = #{lstc_file_path}
WHERE prj_id = #{prj_id}
AND version_number = #{version_number}
AND step_number = #{step_number}

mapper.insert:
INSERT INTO process_comment
(
    comm_id
  , prj_id
  , version_number
  , rgst_user_id
  , step_number
  , comment_description
)
VALUES
(
    #{comm_id}
  , #{prj_id}
  , #{version_number}
  , #{rgst_user_id}
  , #{step_number}
  , #{comment_description}
)

INSERT INTO process_comment_file
(
    comm_file_id
  , comm_id
  , file_path
)
VALUES
(
    #{comm_file_id}
  , #{comm_id}
  , #{file_path}
)
