mapper.update:
UPDATE user_info 
SET isfirst = 0
WHERE login_id = #{login_id}

mapper.insert:
INSERT INTO user_auth
(
  login_id,
  auth_code
)
VALUES
(
  #{login_id},
  #{auth_code}
)
ON DUPLICATE KEY UPDATE
  auth_code = #{auth_code}

mapper.update:
UPDATE user_info
SET login_pw = #{login_pw}
WHERE login_id = #{login_id}

UPDATE user_info
SET user_status = #{user_status}
WHERE login_id = #{login_id}

UPDATE user_info
SET login_pw = #{new_password}
  , isfirst = #{isfirst}
WHERE login_id = #{login_id}
